mapper.update:
UPDATE user SET modify_time = NOW()

  ,username = #{username}


  ,password = #{password}


  ,email = #{email}


  ,realname = #{realname}


  ,role_id = #{roleId}

WHERE id = #{id} AND delete_flag = '0'

mapper.select:
SELECT u.username, u.realname, u.email, u.create_time, u.modify_time, r.id AS role_id, r.name AS role_name FROM user u JOIN role r ON u.role_id = r.id WHERE delete_flag = '0'
AND r.id = #{roleId}
AND u.username LIKE CONCAT('%', #{username}, '%')
AND u.realname LIKE CONCAT('%', #{realname}, '%')
AND u.email LIKE CONCAT('%', #{email}, '%')
ORDER BY modify_time DESC LIMIT #{start},#{pageSize}

